import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./Register.css";
import ReCAPTCHA from "react-google-recaptcha";

const Register = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [email, setEmail] = useState("");
  const [otp, setOtp] = useState("");
  const [isOtpSent, setIsOtpSent] = useState(false);
  const [captchaValue, setCaptchaValue] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();

  const handleSendOtp = async () => {
    try {
      const response = await fetch("http://127.0.0.1:5001/send-otp", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email }),
      });

      const data = await response.json();

      if (response.ok) {
        console.log(data.message);
        setIsOtpSent(true);
        alert("OTP sent successfully. Please check your email.");
      } else {
        console.error(data.error);
        alert("Failed to send OTP: " + data.error);
      }
    } catch (error) {
      console.error("Error sending OTP:", error);
      alert("Error sending OTP. Please try again later.");
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("http://127.0.0.1:5001/register", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ username, password, email, otp }),
      });

      const data = await response.json(); // Parse the JSON response

      // Check if reCAPTCHA is verified
      if (!captchaValue) {
        alert("Please complete the reCAPTCHA verification.");
        return;
      }

      if (response.ok) {
        // Registration successful, show success message
        console.log(data.message);
        alert("Registration Successful");
        localStorage.setItem("token", data.token); // Assuming your server sends a token upon successful registration
        navigate("/login");
      } else if (response.status === 400) {
        // Registration failed due to existing user, show specific error message
        console.error(data.error);
        alert("Registration failed: " + data.error);
      } else {
        // Other registration errors, show a generic error message
        console.error("Registration failed:", response.statusText);
        alert("Registration failed. Please try again later.");
      }
    } catch (error) {
      console.error("Error during registration:", error);
      alert("Error: " + error.message);
    }
  };

  return (
    <div className="form-container">
      <div className="form-header">
        <h2>Register</h2>
      </div>
      <form onSubmit={handleRegister}>
        <div className="form-group">
          <label htmlFor="username">Username:</label>
          <input
            type="text"
            id="username"
            name="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password:</label>
          <div className="password-input">
            <input
              type={showPassword ? "text" : "password"}
              id="password"
              name="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <i
              className={`fas ${
                showPassword ? "fa-eye-slash" : "fa-eye"
              } password-toggle-icon`}
              onClick={() => setShowPassword(!showPassword)}
            ></i>
          </div>
        </div>
        <div className="form-group">
          {/* Google reCAPTCHA */}
          <ReCAPTCHA
            sitekey="6LeIxAcTAAAAAJcZVRqyHh71UMIEGNQ_MXjiZKhI"
            onChange={(value) => setCaptchaValue(value)}
          />
        </div>
        <button type="submit" className="button">
          Register
        </button>
      </form>
      <p>
        Already have an account? <Link to="/login">Login here</Link>
      </p>
    </div>
  );
};

export default Register;
